mapper.update:
update todo set
todo_contents = #{todoContents}, todo_date = #{todoDate}, todo_start_date=#{todoStartDate}, todo_end_date=#{todoEndDate}, todo_alarm = #{todoAlarm}, todo_point = #{todoPoint}
where todo_idx = #{todoIdx}

update todo set
todo_delete = "Y"
where todo_idx = #{todoIdx}

update todo set
todo_alarm = #{todoAlarm}
where todo_idx = #{todoIdx}
